function fetchData() {
  fetch("http://localhost:3000/api/v1/jobs/1")
  .then(response => {
    if (!response.ok) {
      throw Error("An error occurred. Please try again.")
    }
    return response.json()
  }).then(data => {

  var position = data.position;
  document.querySelector('#position').innerHTML = `<h3>${position}</h3>`;

  let firstName = data.shifts.map(data => {
    return `
    <span>
     ${data.user.first_name}
    </span>
     `
    })
    document.querySelector("#first_name").insertAdjacentHTML("afterbegin", firstName);
  console.log(data);

  let timeClock = data.shifts.map(data => {
    console.log(data)
    return `
     <div>
        <p>Clock in</p>
        ${data.clock_in}
     </div>
     <div>
        <p>Clock out</p>
        ${data.clock_out}
     </div>
     `
    })
    document.querySelector("#time_clock").insertAdjacentHTML("afterbegin", timeClock);

    let clocked_out = (data.clock_out === null) && (data.clock_in !== null);
    let timeClockButton = document.querySelector('#time_clock_btn')

    if (clocked_out) {
      timeClockButton.innerText = 'Clock In';
      timeClockButton.style.color = 'green';
    } else {
      timeClockButton.innerText = 'Clock Out';
      timeClockButton.style.background = 'grey';
    }

  }).catch(error => {
    console.log(error)
  });
}

fetchData()
