function fetchData() {
  fetch('http://localhost:3000/api/v1/jobs/1')
  .then(response => {
    if (!response.ok) {
      throw Error('An error occurred. Please try again.')
    }
    return response.json()
  }).then(data => {
    console.log(data.position);
    const html = data.position.map(job => {
      return `<p>Position: ${job.position}</p>`
    })
    console.log(html)
    document.querySelector("#app").insertAdjacentHTML('afterbegin',"<h1>heloo</h1>");
  }).catch(error => {
    console.log(error)
  });
}

fetchData()
