function fetchData() {
  fetch('http://localhost:3000/api/v1/jobs/1')
  .then(response => {
    if (!response.ok) {
      throw Error('An error occurred. Please try again.')
    }
    return response.json()
  }).then(data => {
    console.log(data);
    console.log(data.position);
    document.getElementById('#position').innerHTML = "Position:" + data.position;


    const time_data = data.shifts.map(position => {
      return `<p>Time Clock: ${position.shifts}</p>`
    })
    console.log(time_data)
    document.querySelector("#app").insertAdjacentHTML('afterbegin',"<h1>Time</h1>");

  }).catch(error => {
    console.log(error)
  });
}

fetchData()
