function fetchData() {
  fetch('http://localhost:3000/api/v1/jobs/1')
  .then(response => {
    if (!response.ok) {
      throw Error('An error occurred. Please try again.')
    }
    return response.json()
  }).then(data => {
    console.log(data);


  var position = data.position;
  // prompt(position)

  document.getElementById("#position").innerHTML = "Position:" + position;



    const time_data = data.shifts.map(position => {
      return `<p>Time Clock: ${position.shifts}</p>`
    })
    console.log(time_data)
    document.querySelector("#app").insertAdjacentHTML('afterbegin',time_data);

  }).catch(error => {
    console.log(error)
  });
}

fetchData()
