function fetchData() {
  fetch('http://localhost:3000/api/v1/jobs/1')
  .then(response => {
    if (!response.ok) {
      throw Error('An error occurred. Please try again.')
    }
    return response.json()
  }).then(data => {

  console.log(data.shifts);

  var position = data.position;
  document.querySelector('#position').innerHTML = `<h3>${position}</h3>` ;


  let html = data.shifts.map(data => {
    return `
    <p>
      Name: ${data.user.first_name}
    </p>
      <br />

     Clock-in: ${data.clock_in}
     <br />
     Clock-out: ${data.clock_out}

     `
    })
    // console.log(time_data)
    document.querySelector("#app").insertAdjacentHTML('afterbegin', html);


  }).catch(error => {
    console.log(error)
  });
}

fetchData()
