function fetchData() {
  fetch("http://localhost:3000/api/v1/jobs/1")
  .then(response => {
    if (!response.ok) {
      throw Error("An error occurred. Please try again.")
    }
    return response.json()
  }).then(data => {
  var position = data.position;
  document.querySelector('#position').innerHTML = `<h3>${position}</h3>`;

  var firstName = data.shifts.map(data => {
    return `
    <span>
     ${data.user.first_name}
    </span>
     `
    })
    document.querySelector("#first_name").insertAdjacentHTML("afterbegin", firstName);
    console.log(data);

  var timeClock = data.shifts.map(data => {
    console.log(data)
    return `
     <div>
        <h5>Clock in</h5>
        ${data.clock_in}
     </div>
     <div>
        <h5>Clock out</h5>
        <p>${data.clock_out}</p>

     </div>
     `
    })
    document.querySelector("#time_clock").insertAdjacentHTML("afterbegin", timeClock);
    var clocked_out = (data.clock_out === null) && (data.clock_in !== null);
    var timeClockButton = document.querySelector('#time_clock_btn')

    if (clocked_out) {
      timeClockButton.innerText = 'Clock in';
      // timeClockButton.style.background = 'gray';

    } else {
      timeClockButton.innerText = 'Clock out';
      // timeClockButton.style.background = 'gray';
    }
  }).catch(error => {
    console.log(error)
  });
}

fetchData()
